    <Card className="mb-3">
             <Card.Body>
                
                <Card.Title>Sammanst√§llning</Card.Title>
                    
                <ListGroup variant="flush">
                <ListGroup.Item>
                  <Row>
                    <Col>Produkter</Col>
                    <Col>{order.itemsPrice.toFixed(2)}Kr</Col>
                  </Row>
                </ListGroup.Item>



