                                        <Row>
                                            <Col>Leverans</Col>
                                            <Col>{order.shippingPrice.toFixed(2)}kr</Col>
                                              </Row>
                                    </ListGroup.Item>     
                                            
                                    
                                    <ListGroup.Item>

                                    <Row>
                                <Col>Moms</Col>
                                <Col>{order.taxPrice.toFixed(2)}kr</Col>
                                </Row>
                                    </ListGroup.Item>

