import React, { useContext, useEffect, useReducer } from 'react'
import axios from "axios"
import { Store } from '../Store';
import { getError } from '../utils';
import MessageBox from "../components/MessageBox"
import LoadingBox from '../components/LoadingBox';
import Row from "react-bootstrap/Row"
import Col from "react-bootstrap/Col"
import Card from "react-bootstrap/Card"
const reducer = (state, action) => {
  switch (action.type) {
    case 'FETCH_REQUEST':
      return {...state, loading: true}
 case 'FETCH_SUCCESS':
  return {
    ...state,
    summary: action.payload,
    loading: false
  };
        case 'FETCH_FAIL':
          return {...state,loading:false, error: action.payload};

      default:
        return state
  }
}
export default function DashBoardScreen() {

const [{loading, summary, error}, dispatch] = useReducer(reducer, {
  loading: true,
  error:''
});
const {state} = useContext(Store)
const {userInfo} = state;

useEffect(() => {
const fetchData = async () => {
  try {
        const {data} = await axios.get('/api/orders/summary', {
          headers: {Authorization:`Bearer ${userInfo.token}`},
        });
        dispatch({type:'FETCH_SUCCESS',payload: data})
  } catch(err) {
    dispatch({
      type:'FETCH_FAIL',
      payload: getError(err)
    })
  }
}
fetchData();
},[userInfo])

  return (
    <div>
      <h1>DashBoard</h1>
{loading ? (<LoadingBox/>)
: error ? (<MessageBox variant="danger">{error}</MessageBox>
) : (
  <>
  <Row>
    <Col md={4}>
  <Card>
    <Card.Body>
      <Card.Title>{summary.users && summary.users[0]? summary.users[0].numUsers:0}</Card.Title>
      <Card.Text>Anv√§ndare</Card.Text>
    </Card.Body>
  </Card>
    </Col>
    
    <Col md={4}>
  <Card>
    <Card.Body>
      <Card.Title>{summary.orders && summary.orders[0]?summary.orders[0].numOrders:0}</Card.Title>
      <Card.Text>orders</Card.Text>
    </Card.Body>
  </Card>
    </Col>
    
    <Col md={4}>
  <Card>
    <Card.Body>
      <Card.Title>{summary.orders && 
      summary.orders[0]?summary.orders[0].totalSales
      :0}</Card.Title>
      <Card.Text>orders</Card.Text>
    </Card.Body>
  </Card>
    </Col>
    
    
    </Row>
    
    </>
)}

    </div>
  )
}
