import React, { useContext, useState } from 'react'
import { Helmet } from 'react-helmet-async';
import { Store } from '../Store';
import Form from "react-bootstrap/Form"
import Button from 'react-bootstrap/Button';


export default function ProfileScreen() {
    const {state, dispatch: ctxDispatch} = useContext(Store);
    const {userInfo} = state;
    const [name, setName] = useState(userInfo.name)
    const [email, setEmail] = useState(userInfo.email)
    const [password, setPassword] = useState('')
    const [confirmPassword, setConfirmPassword] = useState('')


    const submitHandler = async (e) =>{
        e.pre
    }
  return (
    <div className='container small-container' >
        <Helmet>
            <title>Användare</title>
        </Helmet>
        <h1 className='my-3'>Min Profil</h1>

        <form onSubmit={submitHandler}>
    <Form.Group className="mb-3" controllid="namn">
        <Form.Label>Namn</Form.Label>
        <Form.Control
            value={name}
            onChange={(e) => setName(e.target.value)}
            requrired
        >

        </Form.Control>
    </Form.Group>

    <Form.Group className="mb-3" controllid="email">
        <Form.Label>Epost</Form.Label>
        <Form.Control
            type='email'
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            requrired
        >

        </Form.Control>
    </Form.Group>

    <Form.Group className="mb-3" controllid="password">
        <Form.Label>Lösenord</Form.Label>
        <Form.Control
            type='password'
            
            onChange={(e) => setPassword(e.target.value)}
            requrired
        >

        </Form.Control>
    </Form.Group>
    <Form.Group className="mb-3 " controllid="password">
        <Form.Label>Bekräfta Lösenord</Form.Label>
        <Form.Control
            type='password'
            
            onChange={(e) => setConfirmPassword(e.target.value)}
            requrired
        >

        </Form.Control>
    </Form.Group>
    <div className='mb-3'>
        <Button className='bg-dark border-light' type="submit">Uppdatera</Button>
    </div>
        </form>
    </div>
  )
}
